media_type.script:
Zabbix.Log(3, 'Zabbix-4_4-PagerDuty-Custom-Webhook Script Entry. Value = '+value);

try {
	// optionally return event tags to Zabbix
        var result = {
		'tags': {}
	},
	params = JSON.parse(value),
	req = new CurlHttpRequest(),
	resp;

        // define required PagerDuty Event API v2 Headers
	req.AddHeader('Content-Type: application/json');
	req.AddHeader('X-Routing-Key: '+params.rulesetToken);

        // define required PagerDuty Event API v2 payload        
        body = {};
        payload = {};
        custom_details = {};

        // Event API v2 custom details - add any desired macros here
        custom_details.hostname = params.hostname;
        custom_details.event_name = params.name;
        custom_details.id = params.id;
        custom_details.status = params.status;
        custom_details.ip = params.ip;
        custom_details.value = params.value;
        custom_details.event_id = params.event_id;
        custom_details.zabbix_severity = params.zabbix_severity;
        custom_details.trigger_expression = params.trigger_expression;
        custom_details.trigger_name = params.trigger_name;
        custom_details.trigger_description = params.trigger_description;
        custom_details.event_tags = params.event_tags;

        // Event API v2 payload
        payload.custom_details = custom_details;
        payload.severity = params.severity;
        // Add business aligned prefix here for context (eg service or application macro)
	payload.summary = params.summary_prefix +" : "+params.summary;
        // PagerDuty CEF fields (show up on Alerts tab)
	payload.source = params.hostname;
	payload.component = params.component;
	payload.group = params.group;
        payload.class = params.class;

        // Event API v2 body
        body.client = params.client;
        body.event_action = params.alert_subject;
        body.dedup_key = params.dedup_key;
        body.client_url = params.url;
        body.images = [];
        body.links = [];
        body.payload = payload;

        // Post Zabbix event to PagerDuty Event API v2
	resp = req.Post('https://events.pagerduty.com/v2/enqueue',JSON.stringify(body));

	if (req.Status() != 202) {
	     throw 'ERROR Posting Event: Response code: '+req.Status();
	}

	resp = JSON.parse(resp);
        Zabbix.Log(3, 'PagerDuty Event API v2 Response: '+JSON.stringify(resp));

        // set an event tag for Zabbix event
        result.tags.PagerDutyEventStatus = resp.status;

} catch (error) {
	Zabbix.Log(3, 'ERROR: PagerDuty Event API v2 Response: '+JSON.stringify({"body": body}));
	Zabbix.Log(3, 'ERROR: PagerDuty Event API v2 Response:: '+error);
        result = {};
}
 
return JSON.stringify(result);
